/**
 * BI020
 *
 * - Redirect from checkout to /add-ons page
 * - Ensure that when user goes to checkout from /add-ons page, they don't then
 *   get redirected back to /add-ons
 */
import * as UC from '../../../../lib/uc-lib';
import {cacheDom} from '../../../../lib/cache-dom';
import * as utils from '../../../../lib/utils';

window.UC = window.UC || {};
window.UC.experiments = window.UC.experiments || {};
window.UC.experiments.BI020 = window.UC.experiments.BI020 || {};
window.UC.experiments.BI020.pollers = window.UC.experiments.BI020.pollers || [];

utils.destroyPollers('BI020');

// ---------------------------------------------------------
// Hold outer ref to jQuery
// ---------------------------------------------------------
let $ = null;

const eventSender = utils.events.setDefaultCategory('BI020-Add-on-page');

/**
 * Product IDs that we want to upsell, given in order shown
 */
const loadedProducts = [
    {
        id: 16,
        image: '/product/p/r/product-cutout-biscuit-card-flower-card_1.jpg',
        name: 'flowers biscuit card',
        price: 9
    },
    {
        id: 782,
        image: '/product/p/r/product-cutout-butterfly-biscuit-card.jpg',
        name: 'butterfly biscuit card',
        price: 9
    },
    {
        id: 220,
        image: '/product/p/r/product-cutout-jolly-ginger-ginger-fleur.jpg',
        name: 'fleur jolly ginger',
        price: 6
    },
    {
        id: 285,
        image: '/product/r/e/resize_happy_jolly_ginger_.jpg',
        name: 'happy ginger jolly ginger',
        price: 6
    },
    {
        id: 315,
        image: '/product/2/0/2017_loyal_jolly_ginger_low_res.jpg',
        name: 'loyal ginger jolly ginger',
        price: 6
    },
    {
        id: 963,
        image: '/product/r/3/r32r32r.jpg',
        name: 'little box bouquet chocolates',
        price: 11.95
    },
    {
        id: 91,
        image: '/product/p/r/product-cutout-champagne-2015.jpg',
        name: 'champagne card',
        price: 9
    },
    {
        id: 918,
        image: '/product/u/n/untitled-1_3.jpg',
        name: 'biscuiteers book of iced gifts',
        price: 20
    },
];

/**
 * Define link to image manipulator
 */
const imageUrlPrefix = `https://thumbor-gc.tomandco.uk/unsafe/trim/fit-in/342x342/center/middle/smart/filters:fill(white):sharpen(0.5,0.5,true)/https://www.biscuiteers.com/static/media/catalog/`;

/**
 * Define checkout target link
 */
const toCheckoutLink = '/checkout?bi20-progressed=1';

/**
 * Entry point for checkout page
 */
const runCheckout = () => {
    console.log('run');
    var urlParam = utils.getUrlParameter('bi20-progressed'),
        isSuccessPage = window.location.pathname.match(/success/i);
    if(!urlParam && !isSuccessPage) {
        eventSender.send(null, 'did-redirect-from-checkout-to-addons');
        window.location = '/add-ons';
    }
};

/**
 * Entry point for add ons page
 */
const run = () => {
    // ---------------------------------------------------------
    // Set up
    // ---------------------------------------------------------
    utils.fullStory('BI020', 'Variation 1');

    document.body.classList.add('bi020');

    cacheDom.get('.page-content .c-12 h1').innerHTML = `
        <img src="data:image/gif;base64," />
    `;
    cacheDom.get('.page-content .c-12 h1').classList.add('center');

    // ---------------------------------------------------------
    // Get root scope for basket $on event handling
    // ---------------------------------------------------------
    const $rootScope = angular.element(document.querySelector('html')).scope();
    if(!$rootScope) {
        angular.reloadWithDebugInfo();

        return;
    }

    // ---------------------------------------------------------
    // Build upsell products markup
    // ---------------------------------------------------------
    if(loadedProducts.length) {
        document.body.classList.add('bi020--loaded');

        eventSender.send(null, 'did-show-products');

        // ---------------------------------------------------------
        // Create HTML
        // ---------------------------------------------------------
        cacheDom.get('.page-content').innerHTML = `
            <div class="bi20-content-wrap c-12">
                <h1 class="bi20-page-title m-t-3">Anything else take your fancy?</h1>
                <p class="bi20-subtitle m-t-6">Give someone you love a little something extra. &nbsp;Here are some of our popular gifts.</p>
                <p class="bi20-no-link-wrap m-t-5">
                    <a class="col-pink" href="${toCheckoutLink}">No thanks, go straight to checkout &gt;</a>
                </p>

                <div class="bi20-products-wrap">

                </div>

                <p class="bi20-go-to-checkout-wrap p-t p-b center">
                    <a class="button button--grey button--bigger b-radius bi20-go-to-checkout bi20-go-to-checkout--bottom" 
                        href="${toCheckoutLink}">Go to checkout</a>
                </p>
            </div>
        `;

        cacheDom.get('.bi20-no-link-wrap a').addEventListener('click', () => {
            eventSender.send(null, 'did-click-go-to-checkout-link');
        });

        cacheDom.get('.bi20-go-to-checkout--bottom').addEventListener('click', () => {
            eventSender.send(null, 'did-click-go-to-checkout-button-bottom');
        });

        // ---------------------------------------------------------
        // Add all loaded products
        // ---------------------------------------------------------
        loadedProducts.forEach((item) => {
            cacheDom.get('.bi20-products-wrap').insertAdjacentHTML('beforeend', `
                <div class="bi20-upsell-item c-3-set p-b block-item c-6-set-s">
                    <div class="bi20-upsell-item__image block-item__image block m-b-2">
                        <a class="block ratio-1-1">
                            <img class="fill" src="${imageUrlPrefix + item.image}" />
                        </a>
                    </div>
                    <div class="bi20-upsell-item__text">
                        ${item.name}
                    </div>
                    <div class="bi20-upsell-item__price col-pink">
                        £${item.price.toFixed(2)}
                    </div>
                    <div class="bi20-upsell-item__addbox">
                        <a 
                            data-bi20prodid="${item.id}"
                            class="bi20-upsell-item__add button--pink button--bigger b-radius p-r-10 p-l-10 inline-block button">Add</a>
                    </div>
                </div>
            `);
        });

        // ---------------------------------------------------------
        // Add basket event handler
        // ---------------------------------------------------------
        [].forEach.call(cacheDom.getAll('.bi20-upsell-item__add'), (item) => {
            item.addEventListener('click', (e) => {
                e.currentTarget.innerText = 'Adding...';

                const prodId = e.currentTarget.dataset['bi20prodid'];
                if(prodId) {
                    tco.get('customer').basket.add({
                        products: [parseInt(prodId)]
                    }, {}, {});
                }
            });
        });
    }

    // ---------------------------------------------------------
    // On add to basket show success message
    // ---------------------------------------------------------
    $rootScope.$on('basket.add', () => {
        eventSender.send(null, 'did-add-to-basket');

        [].forEach.call(cacheDom.getAll('.bi20-upsell-item__add'), (item) => {
            item.innerText = 'Add';
        });

        const titleElm = cacheDom.get('.bi20-page-title');
        if(titleElm) {
            const existingSuccessMessage = cacheDom.get('.bi20-page-success', true);
            if(existingSuccessMessage) {
                existingSuccessMessage.remove();
            }

            titleElm.insertAdjacentHTML('afterend', `
                <div class="bi20-page-success tg20-fadein p-a-4 fs-14 italic center m-b bg-col-grey-80 m-t-5">
                    <p>Successfully added to basket</p>
                    <p class="m-t-4"><a class="button button--grey b-radius bi20-go-to-checkout bi20-go-to-checkout--postadd" 
                        href="${toCheckoutLink}">Go to checkout</a></p>
                </div>
            `);

            cacheDom.get('.bi20-go-to-checkout--postadd').addEventListener('click', () => {
                eventSender.send(null, 'did-click-success-go-to-checkout-button');
            });

            $('body,html').animate({
                scrollTop: $(titleElm).offset().top
            });
        }

        // ---------------------------------------------------------
        // Mini basket buy now link
        // ---------------------------------------------------------
        setTimeout(() => {
            const buyBtn = document.querySelector('.minibasket .button.button--pink');
            if(buyBtn) {
                buyBtn.href = toCheckoutLink;
                buyBtn.addEventListener('click', () => {
                    eventSender.send(null, 'did-go-to-checkout-via-minibasket', '', {
                        sendOnce: true    
                    });
                });
            }
        }, 500);

    });
};

// ---------------------------------------------------------
// Poll required elements - CHECKOUT
// ---------------------------------------------------------
window.UC.experiments.BI020.pollers.push(
    UC.poller([
        () => !!window.location.pathname.match(/\/checkout/i)
    ], function() {
        $ = window.JQSG;
        
        runCheckout();    
    })
);

// ---------------------------------------------------------
// Poll required elements - ADD-ONS
// ---------------------------------------------------------
window.UC.experiments.BI020.pollers.push(
    UC.poller([
        () => !!(angular && window.tco && window.tco.get),
        () => !!window.location.pathname.match(/add-ons/i),
        '.page-content'
    ], function() {
        $ = window.JQSG;
        
        run();    
    })
);
